import React, { useState, useMemo } from "react";
import { useSelector, useDispatch } from "react-redux";
import { deleteTask, updateTask } from "../features/tasksSlice";
import TaskForm from "./TaskForm";
import { DragDropContext, Droppable, Draggable } from "react-beautiful-dnd";
import "bootstrap/dist/css/bootstrap.min.css";

// Main component for displaying and managing tasks
const TaskList = () => {
  // Retrieve tasks from the Redux store
  const tasks = useSelector((state) => state.tasks.tasks);
  const dispatch = useDispatch();

  // State variables for managing task selection and visibility of the task form
  const [selectedTask, setSelectedTask] = useState(null);
  const [isFormVisible, setFormVisible] = useState(false);
  const [search, setSearch] = useState("");

  // Memoized list of tasks filtered by search query
  const filteredTasks = useMemo(() => {
    return tasks.filter((task) =>
      task.title.toLowerCase().includes(search.toLowerCase())
    );
  }, [tasks, search]);

  // Function to handle editing a task
  const handleEdit = (task) => {
    setSelectedTask(task);
    setFormVisible(true); // Show the task form
  };

  // Function to handle task deletion
  const handleDelete = (id) => {
    dispatch(deleteTask(id)); // Dispatch delete action
  };

  // Function to close the task form
  const closeForm = () => {
    setSelectedTask(null);
    setFormVisible(false); // Hide the task form
  };

  // Function to handle drag and drop actions
  const handleDragEnd = (result) => {
    const { destination, source } = result;

    // Check if dropped outside the list or in the same position
    if (
      !destination ||
      (destination.droppableId === source.droppableId &&
        destination.index === source.index)
    ) {
      return;
    }

    // Get the task being moved and the new state from the droppableId
    const taskId = filteredTasks[source.index].id;
    const newState = destination.droppableId;

    // Update the task's state in the Redux store
    dispatch(updateTask({ id: taskId, state: newState }));
  };

  return (
    <DragDropContext onDragEnd={handleDragEnd}>
      <div>
        <div className="container">
          <div className="row d-flex justify-content-around mt-5">
            {/* Button to add a new task */}
            <button
              className="btn btn-primary w-25"
              onClick={() => setFormVisible(true)}
            >
              Add Task
            </button>

            {/* Task form for adding/editing tasks */}
            {isFormVisible && (
              <TaskForm existingTask={selectedTask} onClose={closeForm} />
            )}

            {/* Search input for filtering tasks */}
            <input
              type="text"
              placeholder="Search by task name"
              value={search}
              onChange={(e) => setSearch(e.target.value)}
              className="w-50 form-control"
            />
          </div>
        </div>

        <div className="container-fluid m-4">
          <div style={{ display: "flex", justifyContent: "space-around" }}>
            {/* Droppable areas for different task states */}
            {["todo", "doing", "done"].map((state) => (
              <Droppable key={state} droppableId={state}>
                {(provided) => (
                  <div
                    ref={provided.innerRef}
                    {...provided.droppableProps}
                    className="w-100 p-2 bg-light rounded" // Full width, with Bootstrap classes
                    style={{ maxWidth: "30%" }} // Ensure max width of 30%
                  >
                    <h2>{state.charAt(0).toUpperCase() + state.slice(1)}</h2>
                    <div className="row">
                      <ul className="d-flex justify-content-center">
                        {/* Render each task as a draggable item */}
                        {filteredTasks
                          .filter((task) => task.state === state)
                          .map((task, index) => (
                            <Draggable
                              key={task.id}
                              draggableId={task.id.toString()}
                              index={index}
                            >
                              {(provided) => (
                                <li
                                  className="card p-5 mx-2" // Added mx-2 for spacing between cards
                                  style={{ width: "300px" }} // Adjusted width
                                  ref={provided.innerRef}
                                  {...provided.draggableProps}
                                  {...provided.dragHandleProps}
                                >
                                  <img
                                    src={task.image}
                                    alt={task.title}
                                    className="img-fluid rounded-5" // Use img-fluid for responsive image
                                  />
                                  <div className="card-body">
                                    <h3>{task.title}</h3>
                                    <p>{task.description}</p>
                                    <p>Priority: {task.priority}</p>
                                    <p>State: {task.state}</p>

                                    {/* Buttons for editing and deleting tasks */}
                                    <button
                                      className="btn btn-primary"
                                      onClick={() => handleEdit(task)}
                                    >
                                      Edit
                                    </button>
                                    <button
                                      className="btn btn-danger"
                                      onClick={() => handleDelete(task.id)}
                                    >
                                      Delete
                                    </button>
                                  </div>
                                </li>
                              )}
                            </Draggable>
                          ))}
                      </ul>
                    </div>
                    {provided.placeholder} {/* Placeholder for draggable */}
                  </div>
                )}
              </Droppable>
            ))}
          </div>
        </div>
      </div>
    </DragDropContext>
  );
};

export default TaskList;
