import React from 'react';
import { useForm } from 'react-hook-form'; // Importing react-hook-form for form handling
import { yupResolver } from '@hookform/resolvers/yup'; // Importing yupResolver to integrate Yup with react-hook-form
import * as Yup from 'yup'; // Importing Yup for validation schema
import { useDispatch } from 'react-redux'; // Importing useDispatch for Redux actions
import { addTask, updateTask } from '../features/tasksSlice'; // Importing actions for adding and updating tasks

// Validation schema using Yup
const validationSchema = Yup.object().shape({
  title: Yup.string().required('Title is required'), // Title must be a string and is required
  description: Yup.string().required('Description is required'), // Description must be a string and is required
  priority: Yup.string().required('Priority is required'), // Priority must be selected and is required
  image: Yup.string().url('Invalid URL').required('Image URL is required'), // Image URL must be a valid URL and is required
});

// TaskForm component for adding or editing tasks
const TaskForm = ({ existingTask, onClose }) => {
  const dispatch = useDispatch(); // Hook to access the dispatch function from Redux
  const isEdit = !!existingTask; // Determine if we're editing an existing task

  // Setting up the form using react-hook-form with Yup validation
  const {
    register, // Function to register input fields
    handleSubmit, // Function to handle form submission
    formState: { errors },
  } = useForm({
    resolver: yupResolver(validationSchema),
    defaultValues: existingTask || { title: '', description: '', priority: '', image: '' },
  });

  // Function to handle form submission
  const onSubmit = data => {
    // Preparing data for submission
    const newData = {
      ...data,
      state: isEdit ? existingTask.state : 'todo',
      id: isEdit ? existingTask.id : Date.now(),
    };
    // Dispatching the appropriate action based on edit status
    if (isEdit) {
      dispatch(updateTask(newData));
    } else {
      dispatch(addTask(newData));
    }
    onClose();
  };

  return (
    <form onSubmit={handleSubmit(onSubmit)}> {/* Form submission handling */}
      <div className="col-md-12">
        <div className="row d-flex justify-content-around">
          <div className="col-6">
            {/* Title input field */}
            <input className="form-control w-100 mt-5" type="text" {...register('title')} placeholder="Title" />
            {errors.title && <p>{errors.title.message}</p>} {/* Display error message if title validation fails */}

            {/* Description input field */}
            <input className="form-control w-100 mt-5" type="text" {...register('description')} placeholder="Description" />
            {errors.description && <p>{errors.description.message}</p>} {/* Display error message if description validation fails */}
          </div>

          <div className="col-6">
            {/* Priority selection field */}
            <select className="form-select w-100 mt-5" {...register('priority')}>
              <option value="">Select Priority</option>
              <option value="low">Low</option>
              <option value="medium">Medium</option>
              <option value="high">High</option>
            </select>
            {errors.priority && <p>{errors.priority.message}</p>} {/* Display error message if priority validation fails */}

            {/* Image URL input field */}
            <input className="form-control w-100 mt-5" type="url" {...register('image')} placeholder="Image URL" />
            {errors.image && <p>{errors.image.message}</p>} {/* Display error message if image URL validation fails */}
          </div>
        </div>
      </div>

      {/* Buttons for form submission and cancellation */}
      <div className="d-flex justify-content-around m-4">
        <button type="submit" className="btn btn-outline-success w-25 mt-4">{isEdit ? 'Update Task' : 'Add Task'}</button>
        <button type="button" className="btn btn-outline-danger w-25 mt-4" onClick={onClose}>Cancel</button>
      </div>
    </form>
  );
};

export default TaskForm; // Exporting the TaskForm component
