import React, { useState, useMemo } from "react";
import { useSelector, useDispatch } from "react-redux";
import { deleteTask, updateTask, reorderTask } from "../features/tasksSlice"; // Import reorderTask action
import TaskForm from "./TaskForm";
import { DragDropContext, Droppable, Draggable } from "react-beautiful-dnd";
import "bootstrap/dist/css/bootstrap.min.css";

// Main component for displaying and managing tasks
const TaskList = () => {
  const tasks = useSelector((state) => state.tasks.tasks);
  const dispatch = useDispatch();

  // State variables for managing task selection and visibility of the task form
  const [selectedTask, setSelectedTask] = useState(null);
  const [isFormVisible, setFormVisible] = useState(false);
  const [search, setSearch] = useState("");

  // Memoized list of tasks filtered by search query
  const filteredTasks = useMemo(() => {
    return tasks.filter((task) =>
      task.title.toLowerCase().includes(search.toLowerCase())
    );
  }, [tasks, search]);

  // Function to handle editing a task
  const handleEdit = (task) => {
    setSelectedTask(task);
    setFormVisible(true);
  };

  // Function to handle task deletion
  const handleDelete = (id) => {
    dispatch(deleteTask(id));
  };

  // Function to close the task form
  const closeForm = () => {
    setSelectedTask(null);
    setFormVisible(false);
  };

// Example of using reorderTask in handleDragEnd
const handleDragEnd = (result) => {
    const { destination, source } = result;

    // Check if dropped outside the list or in the same position
    if (!destination || (destination.droppableId === source.droppableId && destination.index === source.index)) {
      return;
    }

    // Get the task ID from the original tasks list
    const taskId = filteredTasks[source.index].id;
    const newState = destination.droppableId;
    const newIndex = destination.index;

    // Update the task's state and reorder in the Redux store
    dispatch(updateTask({ id: taskId, state: newState }));
    dispatch(reorderTask({ id: taskId, newIndex }));
  };



  // Group tasks by their states for rendering
  const groupedTasks = useMemo(() => {
    return ["todo", "doing", "done"].map(state => ({
      state,
      tasks: filteredTasks.filter(task => task.state === state)
    }));
  }, [filteredTasks]);

  return (
    <DragDropContext onDragEnd={handleDragEnd}>
      <div>
        <div className="container">
          <div className="row d-flex justify-content-around mt-5">
            {/* Button to add a new task */}
            <button className="btn btn-primary w-25" onClick={() => setFormVisible(true)}>
              Add Task
            </button>

            {/* Task form for adding/editing tasks */}
            {isFormVisible && <TaskForm existingTask={selectedTask} onClose={closeForm} />}

            {/* Search input for filtering tasks */}
            <input
              type="text"
              placeholder="Search by task name"
              value={search}
              onChange={(e) => setSearch(e.target.value)}
              className="w-50 form-control"
            />
          </div>
        </div>

        <div className="container-fluid m-4">
          <div className="row justify-content-around">
            {/* Droppable areas for different task states */}
            {groupedTasks.map(({ state, tasks }) => (
              <Droppable key={state} droppableId={state}>
                {(provided) => (
                  <div
                    ref={provided.innerRef}
                    {...provided.droppableProps}
                    className="p-2 bg-light rounded border border-secondary"
                    style={{ maxWidth: "30%" }}
                  >
                    <h2 className="text-center">{state.charAt(0).toUpperCase() + state.slice(1)}</h2>
                    <ul className="list-unstyled">
                      {/* Render each task as a draggable item */}
                      {tasks.map((task, index) => (
                        <Draggable key={task.id} draggableId={task.id.toString()} index={index}>
                          {(provided) => (
                            <li
                              className="card mb-3"
                              style={{ width: "100%", minHeight: "120px" }}
                              ref={provided.innerRef}
                              {...provided.draggableProps}
                              {...provided.dragHandleProps}
                            >
                              <img
                                src={task.image}
                                alt={task.title}
                                className="img-fluid rounded-top"
                                style={{ height: "80px", objectFit: "cover" }}
                              />
                              <div className="card-body">
                                <h5 className="card-title">{task.title}</h5>
                                <p className="card-text">{task.description}</p>
                                <p className="card-text"><strong>Priority:</strong> {task.priority}</p>
                                <p className="card-text"><strong>State:</strong> {task.state}</p>

                                {/* Buttons for editing and deleting tasks */}
                                <button className="btn btn-primary me-2" onClick={() => handleEdit(task)}>
                                  Edit
                                </button>
                                <button className="btn btn-danger" onClick={() => handleDelete(task.id)}>
                                  Delete
                                </button>
                              </div>
                            </li>
                          )}
                        </Draggable>
                      ))}
                    </ul>
                    {provided.placeholder} {/* Placeholder for draggable */}
                  </div>
                )}
              </Droppable>
            ))}
          </div>
        </div>
      </div>
    </DragDropContext>
  );
};

export default TaskList;
